// src/App.js
import React, { useState } from 'react';
import { Button, Container } from '@mui/material';
import DataTable from './components/DataTable';
import CrudDialog from './components/CrudDialog';
import ConfirmDialog from './components/ConfirmDialog';

const initialData = [
  { id: 1, name: 'John Doe', email: 'john@example.com' },
  { id: 2, name: 'Jane Smith', email: 'jane@example.com' },
];

function App() {
  const [data, setData] = useState(initialData);
  const [isCrudDialogOpen, setCrudDialogOpen] = useState(false);
  const [isConfirmDialogOpen, setConfirmDialogOpen] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);

  const handleAdd = () => {
    setSelectedRow(null);
    setCrudDialogOpen(true);
  };

  const handleEdit = (row) => {
    setSelectedRow(row);
    setCrudDialogOpen(true);
  };

  const handleDelete = (id) => {
    setData(data.filter((item) => item.id !== id));
    setConfirmDialogOpen(false);
  };

  const handleSave = (row) => {
    if (row.id) {
      setData(data.map((item) => (item.id === row.id ? row : item)));
    } else {
      setData([...data, { id: Date.now(), ...row }]);
    }
    setCrudDialogOpen(false);
  };

  return (
    <Container>
      <Button variant="contained" color="primary" onClick={handleAdd} style={{ margin: '20px 0' }}>
        Add User
      </Button>
      <DataTable data={data} onEdit={handleEdit} onDelete={() => setConfirmDialogOpen(true)} />
      <CrudDialog
        open={isCrudDialogOpen}
        onClose={() => setCrudDialogOpen(false)}
        onSave={handleSave}
        initialData={selectedRow}
      />
      <ConfirmDialog
        open={isConfirmDialogOpen}
        onClose={() => setConfirmDialogOpen(false)}
        onConfirm={() => handleDelete(selectedRow?.id)}
      />
    </Container>
  );
}

export default App;
