// src/components/CrudDialog.js
import React, { useState, useEffect } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField } from '@mui/material';

export default function CrudDialog({ open, onClose, onSave, initialData }) {
  const [id, setId] = useState(null);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    if (initialData) {
      setId(initialData.id); // Set the existing ID when editing
      setName(initialData.name);
      setEmail(initialData.email);
      setDescription(initialData.description || '');
    } else {
      setId(null); // ID should be null for new user
      setName('');
      setEmail('');
      setDescription('');
    }
  }, [initialData]);

  const handleSave = () => {
    // When adding a new user, generate an ID based on the current max ID
    const userData = {
      id: initialData ? initialData.id : null, // Existing ID for editing
      name,
      email,
      description,
    };
    
    onSave(userData);
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>{initialData ? 'Edit User' : 'Add User'}</DialogTitle>
      <DialogContent>
        {!initialData && (
          <TextField
            label="ID (auto-generated)"
            variant="outlined"
            fullWidth
            value={id || "Generating..."}
            disabled
            style={{ marginBottom: '10px' }}
          />
        )}
        <TextField
          label="Name"
          variant="outlined"
          fullWidth
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ marginBottom: '10px' }}
        />
        <TextField
          label="Email"
          variant="outlined"
          fullWidth
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ marginBottom: '10px' }}
        />
        <TextField
          label="Description"
          variant="outlined"
          fullWidth
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{ marginBottom: '10px' }}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">
          Cancel
        </Button>
        <Button onClick={handleSave} color="primary">
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
}
