// src/pages/CrudDashboard.js
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Button, Container } from '@mui/material';
import DataTable from '../components/DataTable';
import CrudDialog from '../components/CrudDialog';
import ConfirmDialog from '../components/ConfirmDialog';
import userData from '../data/userData.json'; // Import JSON data
import { useNavigate } from 'react-router-dom'; // For navigation

export default function CrudDashboard() {
  const navigate = useNavigate();

  // State to manage data
  const [data, setData] = useState([]);
  const [isCrudDialogOpen, setCrudDialogOpen] = useState(false);
  const [isConfirmDialogOpen, setConfirmDialogOpen] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);

  // Fetch data (simulating with JSON file here)
  useEffect(() => {
    // Simulate an API call
    setTimeout(() => {
      setData(userData);
    }, 500);
  }, []);

  // Memoized table data for optimized rendering
  const memoizedData = useMemo(() => data, [data]);

  // Add a new user
  const handleAdd = useCallback(() => {
    setSelectedRow(null); // Clear selected row to indicate new user
    setCrudDialogOpen(true);
  }, []);

  // Edit a user
  const handleEdit = useCallback((row) => {
    setSelectedRow(row); // Set selected user for editing
    setCrudDialogOpen(true);
  }, []);

  // Delete a user
  const handleDelete = useCallback((id) => {
    setData((prevData) => prevData.filter((item) => item.id !== id));
    setConfirmDialogOpen(false);
  }, []);

  // Save user data (add or update)
  const handleSave = useCallback((row) => {
    if (row.id) {
      // Edit existing user
      setData((prevData) => prevData.map((item) => (item.id === row.id ? row : item)));
    } else {
      // Add new user with generated ID (handled in CrudDialog)
      setData((prevData) => [...prevData, row]);
    }
    setCrudDialogOpen(false); // Close the dialog after saving
  }, [data]);

  // Navigate to details page
  const handleViewDetails = useCallback((row) => {
    navigate(`/details/${row.id}`); // Navigate to the details page
  }, [navigate]);

  return (
    <Container>
      <Button
        variant="contained"
        color="primary"
        onClick={handleAdd}
        style={{ margin: '20px 0' }}
      >
        Add User
      </Button>
      <DataTable
        data={memoizedData}
        onEdit={handleEdit}
        onDelete={(id) => {
          setSelectedRow({ id });
          setConfirmDialogOpen(true);
        }}
        onViewDetails={handleViewDetails} // Pass view details handler
      />
      <CrudDialog
        open={isCrudDialogOpen}
        onClose={() => setCrudDialogOpen(false)}
        onSave={handleSave}
        initialData={selectedRow} // Pass selected user data for editing
      />
      <ConfirmDialog
        open={isConfirmDialogOpen}
        onClose={() => setConfirmDialogOpen(false)}
        onConfirm={() => handleDelete(selectedRow?.id)}
      />
    </Container>
  );
}
